import React, { Component } from 'react';
import FurnitureFilter from './FilterComponents/FurnitureFilter';
import FilterResult from './FilterComponents/FilterResult';
import '../HomeComponent/Css/DealsDisplay.css';
import API from '../API_Config';

class Furniture extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedSubCategory: '',
      selectedMinPrice: 0,
      selectedMaxPrice: 200000,
      products: [],
      error: null,
    };
  }

  componentDidMount() {
    this.fetchProducts();
  }

  // Function to fetch products based on filters
  fetchProducts = () => {
    const { selectedSubCategory, selectedMinPrice, selectedMaxPrice } = this.state;
    let apiUrl = `${API}/api/product/byCategory?category=HomeFurniture`;

    // Construct the API URL based on filter selections
    const filterParams = [];

    if (selectedSubCategory) {
      filterParams.push(`subcategory=${selectedSubCategory}`);
    }

    if (selectedMinPrice > 0) {
      filterParams.push(`minprice=${selectedMinPrice}`);
    }

    if (selectedMaxPrice < 200000) {
      filterParams.push(`maxprice=${selectedMaxPrice}`);
    }

    if (filterParams.length > 0) {
      apiUrl += `&${filterParams.join('&')}`;
    }

    fetch(apiUrl)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        this.setState({ products: data, error: null });
      })
      .catch((error) => {
        console.error('Error fetching products:', error);
        this.setState({ error });
      });
  };

  handleSubCategoryChange = (subcategory) => {
    this.setState({ selectedSubCategory: subcategory }, () => {
      this.fetchProducts();
    });
  };

  // Handle price filter change
  handlePriceChange = (minPrice, maxPrice) => {
    this.setState({ selectedMinPrice: minPrice, selectedMaxPrice: maxPrice }, () => {
      this.fetchProducts();
    });
  };

  render() {
    const { selectedSubCategory, selectedMinPrice, selectedMaxPrice, products, error } = this.state;

    return (
      <div>
        <FurnitureFilter
          selectedSubCategory={selectedSubCategory}
          selectedMinPrice={selectedMinPrice}
          selectedMaxPrice={selectedMaxPrice}
          onSubCategoryChange={this.handleSubCategoryChange}
          onPriceChange={this.handlePriceChange}
        />
        {error ? (
          <p>Error fetching products: {error.message}</p>
        ) : (
          <FilterResult products={products} />
        )}
      </div>
    );
  }
}

export default Furniture;
