import React from 'react';
import '../Css/MobileFilter.css';
import all from '../Images/5110770.png';
import HomeOffice from '../Images/homeoffice.png';
import Kitchen from '../Images/kitchen.png';
import Bedroom from '../Images/bedroom.png'
import Bathrooom from '../Images/bathroom.png';
import Outdoor from '../Images/outdoor.png';
import others from '../Images/Other.png'


const FurnitureFilter = ({
    selectedSubCategory,
    selectedPrice,
    onSubCategoryChange,
    onPriceChange
}) => (
    <div className="product-filter">
        <div className='brand-section'>
            <img
                className={`brand-image ${selectedSubCategory === '' ? 'selected' : ''}`}
                src={all}
                alt='all'
                onClick={() => onSubCategoryChange('')}
            />
            <img
                className={`brand-image ${selectedSubCategory === 'homeoffice' ? 'selected' : ''}`}
                src={HomeOffice}
                alt='HomeOffice'
                label='Home & Office'
                onClick={() => onSubCategoryChange('HomeOffice')}
            />
            <img
                className={`brand-image ${selectedSubCategory === 'Kitchen' ? 'selected' : ''}`}
                src={Kitchen}
                alt='Kitchen'
                onClick={() => onSubCategoryChange('Kitchen')}
            />
            <img
                className={`brand-image ${selectedSubCategory === 'BedRoom' ? 'selected' : ''}`}
                src={Bedroom}
                alt='BedRoom'
                onClick={() => onSubCategoryChange('BedRoom')}
            />
            <img
                className={`brand-image ${selectedSubCategory === 'BathRoom' ? 'selected' : ''}`}
                src={Bathrooom}
                alt='BathRoom'
                onClick={() => onSubCategoryChange('BathRoom')}
            />
            <img
                className={`brand-image ${selectedSubCategory === 'Outdoor' ? 'selected' : ''}`}
                src={Outdoor}
                alt='Outdoor'
                onClick={() => onSubCategoryChange('Outdoor')}
            />
            <img
                className={`brand-image ${selectedSubCategory === 'Other' ? 'selected' : ''}`}
                src={others}
                alt='Other'
                onClick={() => onSubCategoryChange('Others')}
            />
        </div>

        <div className="filter-section-price">
            <label>Set Price Range:</label>
            <input
                type="range"
                id="price"
                min="0"
                max="100000"
                step="500"
                value={selectedPrice === null ? 0 : selectedPrice}
                onChange={(e) => onPriceChange(parseInt(e.target.value, 10))}
            />
            <div className="price-label">
                {selectedPrice} /-
            </div>
        </div>
    </div>
);


export default FurnitureFilter;
