import React,{useState} from 'react';
import '../Css/ElectronicsFilter.css';
import all from '../Images/5110770.png'
import Babydress from '../Images/BabyDress.png';
import diapers from '../Images/diapers.png';

const BabyFilter = ({
  selectedSubCategory,
  onSubCategoryChange,
  onPriceChange
}) => {

  const [minPrice, setMinPrice] = useState(0);
  const [maxPrice, setMaxPrice] = useState(200000);
  const handleApply = () => {
    onPriceChange({ minPrice, maxPrice });
  };
  return (
    <div className="product-filter">
      <div className='brand-section'>
        <div className="filter-option">
          <div className={`brand-image ${selectedSubCategory === '' ? 'selected' : ''}`} onClick={() => onSubCategoryChange('')}>
            <img className="filter-image" src={all} alt="all" />
          </div>
          <div className="filter-text">
            <span className="option">All Mobile's</span>
          </div>
        </div>
        <div className="filter-option">
          <div className={`brand-image ${selectedSubCategory === 'BabyDress' ? 'selected' : ''}`} onClick={() => onSubCategoryChange('BabyDress')}>
            <img className="filter-image" src={Babydress} alt="all" />
          </div>
          <div className="filter-text">
            <span className="option">Clothing</span>
          </div>
        </div>
        <div className="filter-option">
          <div className={`brand-image ${selectedSubCategory === 'diapers' ? 'selected' : ''}`} onClick={() => onBrandChange('diapers')}>
            <img className="filter-image" src={diapers} alt="all" />
          </div>
          <div className="filter-text">
            <span className="option">Diapers and Changing</span>
          </div>
        </div>

      </div>

      <div className="filter-section-price">
        <label id='price-label'>Set Price Range:</label>
        <div className="price-filter">
          <input
            type="number"
            id='minValue'
            placeholder="Min price"
            onChange={(e) => setMinPrice(e.target.value)}
            min={0}
          />
          <input
            type="number"
            id='maxValue'
            placeholder="Max price"
            onChange={(e) => setMaxPrice(e.target.value)}
            max={200000}
          />
          <button className='Apply-button' onClick={handleApply}>Apply</button>
        </div>
        <a href='/appliances' className='clear-filter'>Clear filter</a>
      </div>
    </div >
  );
};

export default BabyFilter;
