import React from 'react';
import '../Css/ElectronicsFilter.css';
import all from '../Images/5110770.png';
import men from '../Images/MensFashion.png';
import women from '../Images/WomenFashion3.png';
import kids from '../Images/KidsFashion1.png';
import men_access from '../Images/MensAccessories.png';
import women_access from '../Images/WomensA.png';

const FashionFilter = ({
  selectedSubCategory,
  selectedMinPrice,
  selectedMaxPrice,
  onSubCategoryChange,
  onPriceChange
}) => {
  const handlePriceRangeChange = (e) => {
    const minPrice = parseInt(e.target.value, 10);
    onPriceChange(selectedSubCategory, minPrice, selectedMaxPrice);
  };

  return (
    <div className="product-filter">
      <div className="brand-section">
        {/* Your brand filter options */}
      </div>
      <div className="filter-section-price">
        <label>Set Price Range:</label>
        <div className="price-inputs">
          <input
            type="number"
            placeholder="Min Price"
            value={selectedMinPrice}
            onChange={(e) => onPriceChange(selectedSubCategory, e.target.value, selectedMaxPrice)}
          />
          <span className="price-divider">to</span>
          <input
            type="number"
            placeholder="Max Price"
            value={selectedMaxPrice}
            onChange={(e) => onPriceChange(selectedSubCategory, selectedMinPrice, e.target.value)}
          />
        </div>
        <input
          className="price-bar"
          type="range"
          id="price"
          min="0"
          max="100000"
          step="500"
          value={selectedMinPrice}
          onChange={handlePriceRangeChange}
        />
        <div className="price-label">
          {selectedMinPrice !== null && selectedMaxPrice !== null
            ? `${selectedMinPrice} /- to ${selectedMaxPrice} /-`
            : 'Select a price range'}
        </div>
      </div>
    </div>
  );
};

export default FashionFilter;
