import React from "react";
import logo from '../FooterComponent/Images/X_logo.png';

const JoinTwitter = () => {
  const channelLink = "https://twitter.com/my_twitter_channel";

  const handleTwitterButtonClick = () => {
    window.open(channelLink, "_blank");
  };

  return (
    <button className="twitter-button" onClick={handleTwitterButtonClick}>
      <img src={logo} className='twitter-logo' alt="Twitter logo" />
      <span className="twitter-text">Follow us on Twitter</span>
    </button>
  );
};

export default JoinTwitter;
