import React from 'react';
import './Css/DealsDisplay.css';
function DealsDisplay() {
  const deals = [
    {
      id: 1,
      title: 'Deal 1',
      image: '<a href="https://www.amazon.in/Bosch-TrueMixx-Pro-750-Watt-Grinder/dp/B07NW4TVSN?_encoding=UTF8&pd_rd_w=SaK1b&content-id=amzn1.sym.04eccdc9-b3cd-4cc1-973f-6fc2a5bdb1a2&pf_rd_p=04eccdc9-b3cd-4cc1-973f-6fc2a5bdb1a2&pf_rd_r=6DG3RC6DNZM4SEAG1JFV&pd_rd_wg=FS5Ou&pd_rd_r=7f49ba22-61e6-411e-9f5d-20259dc09858&linkCode=li1&tag=keerthireddy-21&linkId=5f9b61f97334fde1f2a571b40cb87b54&ref_=as_li_ss_il" target="_blank"><img border="0" src="//ws-in.amazon-adsystem.com/widgets/q?_encoding=UTF8&ASIN=B07NW4TVSN&Format=_SL110_&ID=AsinImage&MarketPlace=IN&ServiceVersion=20070822&WS=1&tag=keerthireddy-21" ></a><img src="https://ir-in.amazon-adsystem.com/e/ir?t=keerthireddy-21&l=li1&o=31&a=B07NW4TVSN" width="1" height="1" border="0" alt="" style="border:none !important; margin:0px !important;" />',
      rating: 4.5,
    },
    {
      id: 2,
      title: 'Deal 2',
      image: 'deal2.jpg',
      rating: 3.8,
    },
    // Add more deal objects as needed
  ];

  return (
    <div className="deals-container">
      {deals.map((deal) => (
        <div key={deal.id} className="deal-card">
          <img src={deal.image} alt={deal.title} className="deal-image" />
          <div className="deal-info">
            <h3 className="deal-title">{deal.title}</h3>
            <div className="deal-rating">
              {renderStars(deal.rating)}
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

function renderStars(rating) {
  const fullStars = Math.floor(rating);
  const halfStar = rating - fullStars >= 0.5;
  
  const stars = [];
  for (let i = 1; i <= 5; i++) {
    if (i <= fullStars) {
      stars.push(<span key={i} className="star">&#9733;</span>);
    } else if (halfStar && i === Math.ceil(rating)) {
      stars.push(<span key={i} className="star">&#9733;</span>);
    } else {
      stars.push(<span key={i} className="star">&#9734;</span>);
    }
  }

  return stars;
}

export default DealsDisplay;
