import React from 'react';
import styled from 'styled-components';

const AboutUsContainer = styled.div`
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
`;

const AboutUsHeader = styled.h1`
  text-align: center;
  font-size: 3rem;
  font-weight: 700;
  color: #000;
`;

const AboutUsBody = styled.div`
  font-size: 1.6rem;
  line-height: 2rem;
  color: #333;
`;

const AboutUsMission = styled.p`
  font-weight: 700;
  margin-bottom: 1rem;
`;

const AboutUsValues = styled.ul`
  list-style-type: none;
  padding: 0;
`;

const AboutUsValue = styled.li`
  margin-bottom: 0.5rem;
`;

const AboutUsCallToAction = styled.a`
  display: block;
  width: 100%;
  max-width: 200px;
  margin: 0 auto;
  padding: 1rem 2rem;
  background-color: #000;
  color: #fff;
  text-align: center;
  font-size: 1.6rem;
  font-weight: 700;
  cursor: pointer;

  &:hover {
    background-color: #333;
  }
`;

const AboutUs = () => {
  return (
    <AboutUsContainer>
      <AboutUsHeader>About Us</AboutUsHeader>

      <AboutUsBody>
        <AboutUsMission>
          Our mission is to help people save money by finding the best deals on the
          internet. We believe that everyone should have access to the best deals,
          regardless of their budget or technical expertise.
        </AboutUsMission>

        <AboutUsValues>
          <AboutUsValue>Integrity</AboutUsValue>
          <AboutUsValue>Transparency</AboutUsValue>
          <AboutUsValue>Customer satisfaction</AboutUsValue>
        </AboutUsValues>

        <AboutUsCallToAction href="/">Find the best deals today!</AboutUsCallToAction>
      </AboutUsBody>
    </AboutUsContainer>
  );
};

export default About;
