import React, { Component } from 'react';
import FilterResult from './FilterComponents/FilterResult';
import '../HomeComponent/Css/DealsDisplay.css';
import API from '../API_Config';
import CouponFilter from './FilterComponents/CouponFilter';

class Coupon extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedSubCategory: '',
      products: [],
      error: null,
    };
  }

  componentDidMount() {
    this.fetchProducts();
  }

  // Function to fetch products based on filters
  fetchProducts = () => {
    const { selectedSubCategory } = this.state;
    let apiUrl = `${API}/api/couponcodes/getAllCodes`;

    // Construct the API URL based on filter selections
    const filterParams = [];

    if (selectedSubCategory) {
      filterParams.push(`subcategory=${selectedSubCategory}`);
    }

    if (filterParams.length > 0) {
      apiUrl += `&${filterParams.join('&')}`;
    }

    console.log(apiUrl);

    fetch(apiUrl)
      .then((response) => {
        if (!response.ok) {
          throw  new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        this.setState({ products: data, error: null });
      })
      .catch((error) => {
        console.error('Error fetching products:', error);
        this.setState({ error });
      });
  };

  handleSubCategoryChange = (brand) => {
    this.setState({ selectedSubCategory: brand }, () => {
      this.fetchProducts();
    });
  };

  render() {
    const { selectedSubCategory, products, error } = this.state;

    return (
      <div>
        <CouponFilter
          selectedSubCategory={selectedSubCategory}
          onSubCategoryChange={this.handleSubCategoryChange}
        />
        {error ? (
          <p>Error fetching products: {error.message}</p>
        ) : (
          <FilterResult products={products} />
        )}
      </div>
    );
  }
}

export default Coupon;
