import React, { Component } from 'react';
import FilterResult from './FilterComponents/FilterResult';
import '../HomeComponent/'
import API from '../API_Config';

class Under99 extends Component {
  constructor() {
    super();
    this.state = {
      products: [],
      error: null,
    };
  }

  componentDidMount() {
    this.fetchProducts();
  }

  // Function to fetch products based on filters
  fetchProducts = () => {
   
    let apiUrl = `${API}/api/product/Under99`;
    console.log(apiUrl);

    fetch(apiUrl)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        this.setState({ products: data, error: null });
      })
      .catch((error) => {
        console.error('Error fetching products:', error);
        this.setState({ error });
      });
  };

  render() {
    const {products, error } = this.state;

    return (
      <div>
        
        {error ? (
          <p>Error fetching products: {error.message}</p>
        ) : (
          <FilterResult products={products} />
        )}
      </div>
    );
  }
}

export default Under99;
