import React, { useState } from 'react';

const MobileFilter = ({ onFilterChange }) => {
  const [selectedCompany, setSelectedCompany] = useState('');
  const [selectedPriceRange, setSelectedPriceRange] = useState('');

  const handleCompanyChange = (event) => {
    setSelectedCompany(event.target.value);
  };

  const handlePriceChange = (event) => {
    setSelectedPriceRange(event.target.value);
  };

  const handleFilterClick = () => {
    onFilterChange(selectedCompany, selectedPriceRange);
  };

  return (
    <div>
      <h2>Mobile Filters</h2>
      <div>
        <label htmlFor="companyFilter">Company:</label>
        <select id="companyFilter" onChange={handleCompanyChange}>
          <option value="">All</option>
          <option value="Apple">Apple</option>
          <option value="Samsung">Samsung</option>
          <option value="Google">Google</option>
          {/* Add more company options here */}
        </select>
      </div>
      <div>
        <label htmlFor="priceFilter">Price Range:</label>
        <select id="priceFilter" onChange={handlePriceChange}>
          <option value="">All</option>
          <option value="100-500">$100 - $500</option>
          <option value="501-1000">$501 - $1000</option>
          <option value="1001-2000">$1001 - $2000</option>
          {/* Add more price range options here */}
        </select>
      </div>
      <button onClick={handleFilterClick}>Apply Filters</button>
    </div>
  );
};

export default MobileFilter;
