import React, { Component } from 'react';
import MobileFilter from './FilterComponents/MobileFilter';
import FilterResult from './FilterComponents/FilterResult';
import './Css/Mobiles.css';

class Mobiles extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedBrand: '',
      selectedPrice: 10000,
      products: [],
      error: null,
    };
  }

  componentDidMount() {
    // Fetch initial product data when the component mounts
    this.fetchProducts();
  }

  // Function to fetch products based on filters
  fetchProducts = () => {
    const { selectedBrand, selectedPrice } = this.state;
    let apiUrl = `http://192.168.0.104:8080/api/product/byBrand?productType=Mobile`;

    // Construct the API URL based on filter selections
    const filterParams = [];

    if (selectedBrand) {
      filterParams.push(`brand=${selectedBrand}`);
    }

    if (selectedPrice !== null) {
      filterParams.push(`price=${selectedPrice}`);
    }

    if (filterParams.length > 0) {
      apiUrl += `&${filterParams.join('&')}`;
    }

    console.log(apiUrl);

    fetch(apiUrl)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        this.setState({ products: data, error: null });
      })
      .catch((error) => {
        console.error('Error fetching products:', error);
        this.setState({ error });
      });
  };

  // Handle brand filter change
  handleBrandChange = (event) => {
    this.setState({ selectedBrand: event.target.value }, () => {
      this.fetchProducts();
    });
  };

  // Handle price filter change
  handlePriceChange = (value) => {
    this.setState({ selectedPrice: value }, () => {
      this.fetchProducts();
    });
  };

  render() {
    const { selectedBrand, selectedPrice, products, error } = this.state;

    return (
      <div>
        <MobileFilter
          selectedBrand={selectedBrand}
          selectedPrice={selectedPrice}
          onBrandChange={this.handleBrandChange}
          onPriceChange={this.handlePriceChange}
        />
        {error ? (
          <p>Error fetching products: {error.message}</p>
        ) : (
          <FilterResult products={products} />
        )}
      </div>
    );
  }
}

export default Mobiles;
