import React from 'react'
import { Carousel } from 'react-responsive-carousel';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import store from '../NavbarComponent/Images/99.png';
import Deals from '../NavbarComponent/Images/Deals.png'
import Coming from '../NavbarComponent/Images/Coming.png'
import '../'
 

function ItemCarousel() {

  const fadeAnimationHandler: AnimationHandler = (props, state): AnimationHandlerResponse => {
    const transitionTime = props.transitionTime + 'ms';
    const transitionTimingFunction = 'ease-in-out';

    let slideStyle: React.CSSProperties = {
        position: 'absolute',
        display: 'block',
        zIndex: -2,
        minHeight: '100%',
        opacity: 0,
        top: 0,
        right: 0,
        left: 0,
        bottom: 0,
        transitionTimingFunction: transitionTimingFunction,
        msTransitionTimingFunction: transitionTimingFunction,
        MozTransitionTimingFunction: transitionTimingFunction,
        WebkitTransitionTimingFunction: transitionTimingFunction,
        OTransitionTimingFunction: transitionTimingFunction,
    };

    if (!state.swiping) {
        slideStyle = {
            ...slideStyle,
            WebkitTransitionDuration: transitionTime,
            MozTransitionDuration: transitionTime,
            OTransitionDuration: transitionTime,
            transitionDuration: transitionTime,
            msTransitionDuration: transitionTime,
        };
    }

    return {
        slideStyle,
        selectedStyle: { ...slideStyle, opacity: 1, position: 'relative' },
        prevStyle: { ...slideStyle },
    };
};  
  return (
    <Carousel>
    <div>
        <img src={store} alt=''></img>
        <p className="legend">Legend 1</p>
    </div>
    <div>
        <img src={Deals} alt=''></img>
        <p className="legend">Legend 2</p>
    </div>
    <div>
        <img src={Coming} alt='' ></img>
        <p className="legend">Legend 3</p>
    </div>
</Carousel>
  )
}

export default ItemCarousel
