import React from "react";
import whatsapp from './i'

const ShareProduct = ({ url }) => {
  const isMobile = () => {
    return /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(
      navigator.userAgent
    );
  };
  const handleShare = () => {
    if (isMobile()) {
      // Open WhatsApp on mobile
      window.open(`whatsapp://send?text=${url}`);
    } else {
      // Open WhatsApp Web on desktop
      window.open(`https://web.whatsapp.com/send?text=${url}`);
    }
  };

  return (
    <button type="button" onClick={handleShare} className="btn btn-share">
  <i className="fas fa-share"></i> Share
</button>
  );
};

export default ShareProduct;
