import React, { useState } from 'react';

function FilterComponent({ mobiles, updateMobiles }) {
  const [selectedBrand, setSelectedBrand] = useState('all');

  const handleBrandChange = (event) => {
    const newBrand = event.target.value;
    setSelectedBrand(newBrand);

    // Filter mobiles based on the selected brand
    const filteredMobiles = mobiles.filter((mobile) => {
      if (newBrand === 'all') {
        return true; // Show all mobiles when "All" is selected
      }
      return mobile.brand === newBrand;
    });

    // Update the mobiles list in the parent component
    updateMobiles(filteredMobiles);
  };

  return (
    <div className="filter-container">
      <div className="filter-title">Filters</div>
      <div className="filter-section">
        <label htmlFor="brand-filter">Brand:</label>
        <select
          id="brand-filter"
          value={selectedBrand}
          onChange={handleBrandChange}
        >
          <option value="all">All</option>
          <option value="apple">Apple</option>
          <option value="samsung">Samsung</option>
          <option value="google">Google</option>
        </select>
      </div>
    </div>
  );
}

export default FilterComponent;
