import React from "react";
import { Accordion, AccordionItem, AccordionItemTitle, AccordionItemBody } from "react-accessible-accordion";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  couponContainer: {
    width: "100%",
    maxWidth: "500px",
    margin: "0 auto",
  },
  couponItem: {
    padding: 10,
    borderRadius: 5,
    backgroundColor: "#fff",
    boxShadow: "0 2px 4px rgba(0, 0, 0, 0.25)",
  },
  couponTitle: {
    fontSize: 16,
    fontWeight: "bold",
  },
  couponCode: {
    fontSize: 14,
    color: "#ccc",
  },
}));

const Coupon = ({ couponCodes }) => {
  const classes = useStyles();

  return (
    <div className={classes.couponContainer}>
      <Accordion>
        {couponCodes.map((couponCode, index) => (
          <AccordionItem key={index}>
            <AccordionItemTitle>
              <span className={classes.couponTitle}>{couponCode.company}</span>
            </AccordionItemTitle>
            <AccordionItemBody>
              <span className={classes.couponCode}>{couponCode.code}</span>
            </AccordionItemBody>
          </AccordionItem>
        ))}
      </Accordion>
    </div>
  );
};

export default Coupon;
