import React from 'react';
import ItemCarousel from './ItemCarousel';
import BrandBoard from './BrandBoard';
import ProductBoard from './ProductBoard';
import DealsDisplay from './DealsDisplay';
import './Css/Home.css';
import { useHistory,useEffect } from 'react-router-dom';

function Home() {
    const history = useHistory();

  useEffect(() => {
    // Simulate server down condition (replace with your actual server check)
    const isServerDown = true;

    if (isServerDown) {
      // Redirect to the server down error page
      history.push('/server-down');
    }
  }, []);
    return (
        <div className="home-main-div">
            <div className="main" >
                <BrandBoard />
                <ItemCarousel />
                <ProductBoard />
            </div>
            <div className='deals-menu'>
                <DealsDisplay />
            </div>
        </div>
    );
}

export default Home;
