import React, { useState, useEffect } from 'react';
import MobileFilter from './FilterComponents/MobileFilter';

const Mobiles = () => {
  const [mobiles, setMobiles] = useState([]);
  const [selectedCompany, setSelectedCompany] = useState('');
  const [selectedPriceRange, setSelectedPriceRange] = useState('');

  useEffect(() => {
    // Make an API request to fetch mobile data based on selected filters
    // You can replace this with your actual API endpoint
    fetch(`/api/mobiles?company=${selectedCompany}&priceRange=${selectedPriceRange}`)
      .then((response) => response.json())
      .then((data) => {
        setMobiles(data);
      });
  }, [selectedCompany, selectedPriceRange]);

  const handleFilterChange = (company, priceRange) => {
    setSelectedCompany(company);
    setSelectedPriceRange(priceRange);
  };

  return (
    <div>
      <MobileFilter onFilterChange={handleFilterChange} />
      <h2>List of Mobiles</h2>
      <ul>
        {mobiles.map((mobile) => (
          <li key={mobile.id}>{mobile.name} - Price: ${mobile.price}</li>
        ))}
      </ul>
    </div>
  );
};

export default Mobiles;
