import React, { useRef } from 'react';
import Carousel from 'react-elastic-carousel';
import './ItemCarousel.css';
import { handleClick } from 'infinite-react-carousel/lib/carousel/listener';

function ItemCarousel() {
  const items = [
    { id: 1, content: 'Item 1' },
    { id: 2, content: 'Item 2' },
    { id: 3, content: 'Item 3' },
    { id: 4, content: 'Item 4' },
    // Add more items as needed
  ];

  const carouselRef = useRef(null);

  const handleNextEnd = (currentItem) => {
    if (currentItem === items.length - 1) {
      carouselRef.current.goTo(0);
    }
  };

  const handleClick=(currentItem) =>{
    alert()
  }

  return (
    <div className="carousel-container">
      <Carousel
        ref={carouselRef}
        enableAutoPlay={true}
        autoPlaySpeed={1000}
        pagination={true}
        isRTL={false}
        enableSwipe={true}
        enableTilt={true}
        itemsToScroll={1}
        isInfinite={true}
        onNextEnd={handleNextEnd}
        onClick={handleClick(currentItem)}
      >
      
      </Carousel>
    </div>
  );
}

export default ItemCarousel;
