import React, { useState, useEffect } from 'react';
import ImageGallery from "react-image-gallery";
import "react-image-gallery/styles/css/image-gallery.css";
import './ItemCarousel.css';

function ItemCarousel() {

  const [data, setData] = useState([]);
  const [index, setIndex] = useState(0); // Index state to keep track of the current image index

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://10.10.11.208:8080/api/news');
        const jsonData = await response.json();
        if (Array.isArray(jsonData)) {
          setData(jsonData);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
        setData([]);
      }
    };
    fetchData();
  }, []);

  const handleChangeIndex = (selectedIndex) => {
    setIndex(selectedIndex);
  };

  const handleClick = () => {
    const selectedItem = data[index];
    if (selectedItem) {
      console.log(selectedItem);
    }
  };

  return (
    <div className="carousel-container">
      <ImageGallery
        items={data.map(item => ({
          id: item.newsId,
          original: item.imageUrl,
          thumbnailLabel: item.title
        }))}
        onClick={handleClick}
        onSlide={(currentIndex) => handleChangeIndex(currentIndex)}
        currentIndex={index}
        slidesPerView={1}
        spaceBetween={10}
        autoPlay={true}
        disableKeyDown={true}
        autoplayTimeout={5000}
        loop={true}
        additionalClass="image-gallery-custom-class"
      />
    </div>
  );
}

export default ItemCarousel;
