import React, { useState, useEffect } from "react";

const Mobile = () => {
  const [items, setItems] = useState([]);
  const [brand, setBrand] = useState("");
  const [price, setPrice] = useState(0);

  useEffect(() => {
    // Get the list of items from the API.
    fetch(`http://192.168.0.101:8080/api/product/byBrand?productType=Mobile&brand=${selectedBrand}&price=${selectedPrice}`)
      .then((response) => response.json())
      .then((data) => setItems(data));
  }, []);

  const handleBrandChange = (event) => {
    // Set the brand filter.
    setBrand(event.target.value);
  };

  const handlePriceChange = (event) => {
    // Set the price filter.
    setPrice(event.target.value);
  };

  const filteredItems = items.filter((item) => {
    // Filter the items by brand.
    if (brand !== "") {
      return item.brand === brand;
    }

    // Filter the items by price.
    if (price !== 0) {
      return item.price <= price;
    }

    // Return all items if no filters are applied.
    return true;
  });

  return (
    <div>
      <h1>Mobile Phones</h1>
      <div>
        <label>Brand:</label>
        <select value={brand} onChange={handleBrandChange}>
          <option value="">All Brands</option>
          {Object.keys(items).map((brand) => (
            <option key={brand} value={brand}>
              {brand}
            </option>
          ))}
        </select>
      </div>
      <div>
        <label>Price:</label>
        <input type="number" value={price} onChange={handlePriceChange} />
      </div>
      <ul>
        {filteredItems.map((item) => (
          <li key={item.id}>
            {item.name} - ${item.price}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Mobile;
