import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import ImageGallery from 'react-image-gallery';
import 'react-image-gallery/styles/css/image-gallery.css';
import './ItemCarousel.css';

function ItemCarousel() {
  const [data, setData] = useState([]);
  const [index, setIndex] = useState(0);
  const [isLoading, setIsLoading] = useState(true); // Add a loading state

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://10.10.11.208:8080/api/news');
        const jsonData = await response.json();
        if (Array.isArray(jsonData)) {
          setData(jsonData);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
        setData([]);
      }
      setIsLoading(false); // Set loading state to false after fetching
    };
    fetchData();
  }, []);

  const handleChangeIndex = (selectedIndex) => {
    setIndex(selectedIndex);
  };

  return (
    <div className="carousel-container">
      {isLoading ? ( // Show loading indicator or message while data is being fetched
        <p>Loading...</p>
      ) : (
        data.length > 0 && (
          <Link to={`${data[index].newsId}`}>
            <ImageGallery
              items={data.map((item) => ({
                id: item.newsId,
                original: item.imageUrl,
                thumbnailLabel: item.title,
              }))}
              currentIndex={index}
              onClick={(currentIndex) => handleChangeIndex(currentIndex)}
              slidesPerView={1}
              spaceBetween={10}
              autoPlay={true}
              disableKeyDown={true}
              autoplayTimeout={5000}
              loop={true}
              additionalClass="image-gallery-custom-class"
            />
          </Link>
        )
      )}
    </div>
  );
}

export default ItemCarousel;
