// MobileFilter.js
import React from 'react';

const MobileFilter = ({ onFilterChange }) => {
  // Define your static data for filters
  const companies = ['Apple', 'Samsung', 'Google', 'OnePlus'];
  const priceRanges = ['Below $300', '$300 - $500', '$500 - $800', 'Above $800'];

  const handleFilterChange = (e) => {
    // Handle filter changes and pass them to the parent component
    const selectedFilters = {
      company: e.target.company.value,
      priceRange: e.target.priceRange.value,
    };
    onFilterChange(selectedFilters);
  };

  return (
    <div className="mobile-filter">
      <form onSubmit={handleFilterChange}>
        <div className="filter-item">
          <label htmlFor="company">Company:</label>
          <select name="company" id="company">
            <option value="">All</option>
            {companies.map((company) => (
              <option key={company} value={company}>
                {company}
              </option>
            ))}
          </select>
        </div>
        <div className="filter-item">
          <label htmlFor="priceRange">Price Range:</label>
          <select name="priceRange" id="priceRange">
            <option value="">All</option>
            {priceRanges.map((range) => (
              <option key={range} value={range}>
                {range}
              </option>
            ))}
          </select>
        </div>
        <button type="submit">Apply Filters</button>
      </form>
    </div>
  );
};

export default MobileFilter;
