import React, { Component } from 'react';
import FashionFilter from './FilterComponents/FashionFilter';
import FilterResult from './FilterComponents/FilterResult';
import './Css/Mobiles.css';
import API from '../API_Config';

class Under99 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedSubCategory: '',
      selectedPrice: 25000,
      products: [],
      error: null,
    };
  }

  componentDidMount() {
    
    this.fetchProducts();
  }

  // Function to fetch products based on filters
  fetchProducts = () => {
    const { selectedSubCategory, selectedPrice } = this.state;
    let apiUrl = `${API}/api/product/Under99`;

    // Construct the API URL based on filter selections
    const filterParams = [];


    console.log(apiUrl);

    fetch(apiUrl)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        this.setState({ products: data, error: null });
      })
      .catch((error) => {
        console.error('Error fetching products:', error);
        this.setState({ error });
      });
  };

  // // Handle brand filter change
  // handleBrandChange = (event) => {
  //   this.setState({ selectedSubCategory: event.target.value }, () => {
  //     this.fetchProducts();
  //   });
  // };

  handleSubCategoryChange = (brand) => {
    this.setState({ selectedSubCategory: brand }, () => {
      this.fetchProducts();
    });
  };

  // Handle price filter change
  handlePriceChange = (value) => {
    this.setState({ selectedPrice: value }, () => {
      this.fetchProducts();
    });
  };

  render() {
    const { selectedSubCategory, selectedPrice, products, error } = this.state;

    return (
      <div>
        <FashionFilter
          selectedSubCategory={selectedSubCategory}
          selectedPrice={selectedPrice}
          onSubCategoryChange={this.handleSubCategoryChange}
          onPriceChange={this.handlePriceChange}
        />
        {error ? (
          <p>Error fetching products: {error.message}</p>
        ) : (
          <FilterResult products={products} />
        )}
      </div>
    );
  }
}

export default Under99;
