import React,{users} from 'react'
import { useParams } from 'react-router-dom';

function SelectBrand() {
  const [deals, setDeals] = useState([
    {
        id: 1,
        title: 'Bosch TrueMixx Pro Mixer Grinder MGM8842MIN, 1000W 4 Jars Black',
        image: 'https://m.media-amazon.com/images/I/81OFmyRxPmL._SX522_.jpg',
        dealPrice: '6449',
        regularPrice: '10590',
        rating: 4.5,
        dis: "Camera: 50MP Main Camera with Sony IMX890 (OIS supported), 48MP Ultrawide Camera with Sony IMX581 (FOV: 115 degree) and 32MP Telephoto lens with Sony IMX709 (2X Optical Zoom); 16MP Front (Selfie) Camera with EIS support",
        url: 'https://amzn.to/3QXmHpI'
    },
    {
        id: 2,
        title: 'Deal 2',
        image: 'https://m.media-amazon.com/images/I/414+xRBltFL._MCnd_AC_.jpg',
        rating: 3.8,
        dealPrice: '5000',
        regularPrice: '7500',
        dis: 'Uniquely designed blunt PoundingBlade with thick edges which replicates pounding effect on dry ingredients, thus giving authentic texture and taste',
    },
    {
        id: 3,
        title: 'Deal 2',
        image: 'https://m.media-amazon.com/images/I/414+xRBltFL._MCnd_AC_.jpg',
        rating: 3.8,
        dealPrice: '5000',
        regularPrice: '7500',
        dis: 'Uniquely designed blunt PoundingBlade with thick edges which replicates pounding effect on dry ingredients, thus giving authentic texture and taste',
    },
    {
        id: 4,
        title: 'Deal 2',
        image: 'https://m.media-amazon.com/images/I/414+xRBltFL._MCnd_AC_.jpg',
        rating: 3.8,
        dis: 'Uniquely designed blunt PoundingBlade with thick edges which replicates pounding effect on dry ingredients, thus giving authentic texture and taste',
    },
    {
        id: 5,
        title: 'Deal 2',
        image: 'https://m.media-amazon.com/images/I/414+xRBltFL._MCnd_AC_.jpg',
        rating: 3.8,
        dis: 'Uniquely designed blunt PoundingBlade with thick edges which replicates pounding effect on dry ingredients, thus giving authentic texture and taste',
    },
    {
        id: 6,
        title: 'Deal 2',
        image: 'https://m.media-amazon.com/images/I/414+xRBltFL._MCnd_AC_.jpg',
        rating: 3.8,
        dis: 'Uniquely designed blunt PoundingBlade with thick edges which replicates pounding effect on dry ingredients, thus giving authentic texture and taste',
    },
    {
        id: 7,
        title: 'Deal 2',
        image: 'https://m.media-amazon.com/images/I/414+xRBltFL._MCnd_AC_.jpg',
        rating: 3.8,
        dis: 'Uniquely designed blunt PoundingBlade with thick edges which replicates pounding effect on dry ingredients, thus giving authentic texture and taste',
    },

    // Add more deal objects as needed
]);
const [currentPage, setCurrentPage] = useState(1);
const dealsPerPage = 6;

const indexOfLastDeal = currentPage * dealsPerPage;
const indexOfFirstDeal = indexOfLastDeal - dealsPerPage;
const currentDeals = deals.slice(indexOfFirstDeal, indexOfLastDeal);

function toggleDescription(dealId) {
    const updatedDeals = deals.map((deal) => {
        if (deal.id === dealId) {
            return { ...deal, showFullDescription: true };
        }
        return deal;
    });

    setDeals(updatedDeals);
}

function goToPage(pageNumber) {
    setCurrentPage(pageNumber);
}

function goToPreviousPage() {
    if (currentPage > 1) {
        setCurrentPage(currentPage - 1);
    }
}

function goToNextPage() {
    if (currentPage < Math.ceil(deals.length / dealsPerPage)) {
        setCurrentPage(currentPage + 1);
    }
}

return (
    <div>
        <div className="deals-container">
            {currentDeals.map((deal) => (
                <div key={deal.id} className="deal-card">
                    <a href={deal.url} target="_blank" rel="noopener noreferrer">
                    <img src={deal.image} alt={deal.title} className="deal-image" />
                        <h3 className="deal-title">{deal.title}</h3>
                        <div className="deal-rating">
                            {renderStars(deal.rating)}
                        </div>
                        <div className="deal-info">
                            <div className="reqular-price">
                                Rs.{deal.regularPrice}/-
                            </div>
                            <div className="deal-price">
                                Rs.{deal.dealPrice}/-
                            </div>
                            <span className="offer-label">
                                {Math.round(((deal.regularPrice - deal.dealPrice) / deal.regularPrice) * 100)}% Off
                            </span>
                        </div>
                    </a>
                </div>
            ))}
        </div>
        <div className="pagination">
            <button
                onClick={goToPreviousPage}
                className="pagination-button arrow-button"
                disabled={currentPage === 1}
            >
                &#8249;
            </button>
            {Array.from({ length: Math.ceil(deals.length / dealsPerPage) }, (_, index) => (
                <button
                    key={index + 1}
                    onClick={() => goToPage(index + 1)}
                    className={`pagination-button ${currentPage === index + 1 ? 'active' : ''}`}
                >
                    {index + 1}
                </button>
            ))}
            <button
                onClick={goToNextPage}
                className="pagination-button arrow-button"
                disabled={currentPage === Math.ceil(deals.length / dealsPerPage)}
            >
                &#8250;
            </button>
        </div>
    </div>
);
}
function renderStars(rating) {
const fullStars = Math.floor(rating);
const halfStar = rating - fullStars >= 0.5;

const stars = [];
for (let i = 1; i <= 5; i++) {
    if (i <= fullStars) {
        stars.push(<span key={i} className="star">&#9733;</span>);
    } else if (halfStar && i === Math.ceil(rating)) {
        stars.push(<span key={i} className="star">&#9733;</span>);
    } else {
        stars.push(<span key={i} className="star">&#9734;</span>);
    }
}

return stars;
}


export default SelectBrand
