import React from 'react';
import './Header.css';
import searchIcon from './search.svg';
import { Link } from 'react-router-dom'
import Logo from '.'

const Header = () => {
    return (
        <header>
            <div className="header-fill">
                <div className="logo">
                    <img src={logo} alt="Logo" />
                </div>

                <div className="search-bar">
                    <input id="search-text" type="text" placeholder="Search for Products, Brands and More..." />
                    <img src={searchIcon} alt="Search" className="search-icon" />
                </div>
                <div className='nav-div'>
                    <nav className="nav-options">
                        <ul>
                            <li className="selected"><Link to="/about">About Us</Link></li>
                            <li className="selected"><Link to="/contact">Contact Us</Link></li>
                        </ul>
                    </nav>
                </div>
            </div>
        </header>
    );
};

export default Header;
