import React, { useState, useEffect } from 'react';
import ImageGallery from 'react-image-gallery';
import 'react-image-gallery/styles/css/image-gallery.css';
import './ItemCarousel.css';

function ItemCarousel() {
  const [data, setData] = useState([]);
  const [index, setIndex] = useState(0);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://10.10.11.208:8080/api/news');
        const jsonData = await response.json();
        if (Array.isArray(jsonData)) {
          setData(jsonData);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
        setData([]);
      }
      setIsLoading(false);
    };
    fetchData();
  }, []);

  const handleChangeIndex = (selectedIndex) => {
    setIndex(selectedIndex);
  };

  const renderCustomSlide = (item) => {
    return (
      <a href={`/${item.newsId}`}>
        <img src={item.original} alt={item.thumbnailLabel} />
      </a>
    );
  };

  return (
    <div className="carousel-container">
      {isLoading ? (
        <p>Loading...</p>
      ) : (
        data.length > 0 && (
          <ImageGallery
            items={data.map((item) => ({
              id: item.newsId,
              original: item.imageUrl,
              thumbnailLabel: item.title,
            }))}
            currentIndex={index}
            onClick={(currentIndex) => handleChangeIndex(currentIndex)}
            renderItem={renderCustomSlide} // Use custom rendering function
            slidesPerView={1}
            spaceBetween={10}
            autoPlay={true}
            disableKeyDown={true}
            autoplayTimeout={5000}
            loop={true}
            additionalClass="image-gallery-custom-class"
          />
        )
      )}
    </div>
  );
}

export default ItemCarousel;
