import React, { Component } from "react";
import { Radio, Input } from "react-bootstrap";

class MobileFilter extends Component {
  constructor(props) {
    super(props);
    this.state = {
      company: "",
      price: "",
    };
  }

  render() {
    const { company, price } = this.state;
    return (
      <div className="mobile-filter">
        <label>Company</label>
        <RadioGroup name="company" value={company} onChange={this.handleCompanyChange}>
          <Radio value="Apple">Apple</Radio>
          <Radio value="Samsung">Samsung</Radio>
          <Radio value="Huawei">Huawei</Radio>
          <Radio value="Xiaomi">Xiaomi</Radio>
        </RadioGroup>
        <label>Price</label>
        <Input type="number" name="price" value={price} onChange={this.handlePriceChange} />
      </div>
    );
  }

  handleCompanyChange = (e) => {
    this.setState({ company: e.target.value });
  };

  handlePriceChange = (e) => {
    this.setState({ price: e.target.value });
  };
}

export default MobileFilter;
