import React from 'react';
import '../Css/ElectronicsFilter.css';
import all from '../Images/5110770.png';
import men from '../Images/MensFashion.png';
import women from '../Images/WomenFashion3.png';
import kids from '../Images/KidsFashion1.png';
import men_access from '../Images/MensAccessories.png';
import women_access from '../Images/WomensA.png';


const FashionFilter = ({
  selectedSubCategory = '',
  selectMinPrice,
  selectMaxPrice,
  onSubCategoryChange,
  onApplyPriceFilter
}) => {

  const handleApplyPriceFilter = (event) => {
    event.preventDefault();
    onApplyPriceFilter(selectMinPrice, selectMaxPrice);
  };

  return (
    <div className="product-filter">
      <div className='brand-section'>
        {['', 'Mens', 'Womens', 'Kids', 'Mens_accessories', 'Womens_accessories'].map((subCategory, index) => (
          <div className="filter-option" key={index}>
            <div className={`brand-image ${selectedSubCategory === subCategory ? 'selected' : ''}`} onClick={() => onSubCategoryChange(subCategory)}>
              <img className="filter-image" src={all} alt="all" />
            </div>
            <div className="filter-text">
              <span className="option">{subCategory}</span>
            </div>
          </div>
        ))}
      </div>
      <div className="filter-section-price">
        <label>Set Price Range:</label>
        <div className="price-inputs">
          <input type="text" placeholder="Min Price" />
          <span className="price-divider">to</span>
          <input type="text" placeholder="Max Price" />
          <button onClick={handleApplyPriceFilter}>Apply</button>
        </div>
      </div>
    </div >
  );
};


export default FashionFilter;
