import React from 'react';
import ItemCarousel from './ItemCarousel';
import BrandBoard from './BrandBoard';
import ProductBoard from './ProductBoard';
import DealsDisplay from './DealsDisplay';
import './Css/Home.css';
import { useHistory,useEffect,useState } from 'react-router-dom';
function Home() {
    const navigate = useNavigate(); // Get the history object from the router
    const [isServerDown, setIsServerDown] = useState(false);
    useEffect(() => {
      // Make an HTTP request to the server to check its status
      fetch('/status')
        .then(response => {
          if (response.status !== 200) {
            setIsServerDown(true); 
            history.push('/server-down');
          }
        })
        .catch(error => {
          setIsServerDown(true);
          history.push('/server-down');
        });
    }, []);
    
    return (
        <div className="home-main-div">
            <div className="main" >
                <BrandBoard />
                <ItemCarousel />
                <ProductBoard />
            </div>
            <div className='deals-menu'>
                <DealsDisplay />
            </div>
        </div>
    );
}

export default Home;
