import React from 'react';
import '../Css/MobileFilter.css';

const MobileFilter = ({
  selectedBrand,
  selectedPrice,
  onBrandChange,
  onPriceChange
}) => (
  <div className="product-filter">
    <h1>Product Filters</h1>
    <div className="filter-section">
      <div className='brand-section'>
      <label htmlFor="brand">By Brand:</label>
     
           <img
            className={`brand-image ${selectedBrand === 'realme' ? 'selected' : ''}`}
            src='https://rukminim2.flixcart.com/fk-p-flap/240/240/image/059c763612908e31.jpg?q=90'
            alt='realme'
            onClick={() => onBrandChange('realme')}
          />
           <img
            className={`brand-image ${selectedBrand === 'vivo' ? 'selected' : ''}`}
            src='https://rukminim2.flixcart.com/fk-p-flap/240/240/image/b9a573a09e83cf82.jpg?q=90'
            alt='vivo'
            onClick={() => onBrandChange('vivo')}
          />
           <img
            className={`brand-image ${selectedBrand === 'samsung' ? 'selected' : ''}`}
            src='https://rukminim2.flixcart.com/fk-p-flap/240/240/image/059c763612908e31.jpg?q=90'
            alt='samsung'
            onClick={() => onBrandChange('samsung')}
          />
           <img
            className={`brand-image ${selectedBrand === 'oppo' ? 'selected' : ''}`}
            src='https://rukminim2.flixcart.com/fk-p-flap/240/240/image/059c763612908e31.jpg?q=90'
            alt='oppo'
            onClick={() => onBrandChange('oppo')}
          />
           <img
            className={`brand-image ${selectedBrand === 'apple' ? 'selected' : ''}`}
            src='https://rukminim2.flixcart.com/fk-p-flap/240/240/image/059c763612908e31.jpg?q=90'
            alt='apple'
            onClick={() => onBrandChange('apple')}
          />

      <img className='images'  src='https://rukminim2.flixcart.com/fk-p-flap/240/240/image/b9a573a09e83cf82.jpg?q=90' alt='vivo' value='vivo'></img>
      <img className='images'  src='https://rukminim2.flixcart.com/fk-p-flap/240/240/image/ef86060ca7c7b895.jpg?q=90' alt='samsung' value='samsung'></img>
      <img className='images'  src='https://rukminim2.flixcart.com/fk-p-flap/240/240/image/dc8a36bb50c1d96a.jpg?q=90' alt='poco' value='poco'></img>
      <img className='images'  src='https://rukminim2.flixcart.com/fk-p-flap/240/240/image/70db8464d13e9b83.jpg?q=90' alt='oppo' value='oppo'></img>
      <img className='images'  src='https://rukminim2.flixcart.com/fk-p-flap/240/240/image/f29ce08479bfb046.jpg?q=90' alt='apple' value='apple'></img>
      <select id="brand" value={selectedBrand} onChange={onBrandChange}>
        <option value="">All Brands</option>
        <option value="Oppo">Oppo</option>
        <option value="Apple">Apple</option>
        {/* Add more brand options as needed */}
      </select>
    </div>
    </div>
    <div className="filter-section">
      <label>By Price:</label>
      <input
        type="range"
        id="price"
        min="0"
        max="100000"
        step="500"
        value={selectedPrice === null ? 0 : selectedPrice}
        onChange={(e) => onPriceChange(parseInt(e.target.value, 10))}
      />
      <div className="price-label">
        {selectedPrice} /-
      </div>
    </div>
  </div>
);

// MobileFilter.defaultProps = {
//   selectedPrice: 100000
// };

export default MobileFilter;
