import React from 'react';
import '../Css/MobileFilter.css';
import all from '../Images/5110770.png';
import AmazonFresh from '../../HomeComponent/Images/a';
import FlipkartSupermarket from '../../HomeComponent/Images/Flipkart_logo.svg.png';
import BigBusket from '../../HomeComponent/Images/BigBasket.jpg';
import Zepto from '../../HomeComponent/Images/Zepto.png';
import SwiggyInstamart from '../../HomeComponent/Images/Swiggy_logo.svg.png';
import Blinkit from '../../HomeComponent/Images/Blinkit-yellow-rounded.svg.png';
import JioMart from '../../HomeComponent/Images/JioMart.png';


const GroceryFilter = ({
  selectedSubCategory,
  selectedPrice,
  onSubCategoryChange,
  onPriceChange
}) => (
  <div className="product-filter">
    <div className="filter-section-brand">
      <div className='brand-section'>
      <img
          className={`brand-image ${selectedSubCategory === '' ? 'selected' : ''}`}
          src={all}
          alt='all'
          onClick={() => onSubCategoryChange('')}
        />

        <img
          className={`brand-image ${selectedSubCategory === 'AmazonFresh' ? 'selected' : ''}`}
          src={AmazonFresh}
          alt='AmazonFresh'
          onClick={() => onSubCategoryChange('AmazonFresh')}
        />
        <img
          className={`brand-image ${selectedSubCategory === 'FlipkartSupermarket' ? 'selected' : ''}`}
          src={FlipkartSupermarket}
          alt='FlipkartSupermarket'
          onClick={() => onSubCategoryChange('FlipkartSupermarket')}
        />
        <img
          className={`brand-image ${selectedSubCategory === 'BigBusket' ? 'selected' : ''}`}
          src={BigBusket}
          alt='BigBusket'
          onClick={() => onSubCategoryChange('BigBusket')}
        />
        <img
          className={`brand-image ${selectedSubCategory === 'Zepto' ? 'selected' : ''}`}
          src={Zepto}
          alt='Zepto'
          onClick={() => onSubCategoryChange('Zepto')}
        />
        <img
          className={`brand-image ${selectedSubCategory === 'SwiggyInstamart' ? 'selected' : ''}`}
          src={SwiggyInstamart}
          alt='SwiggyInstamart'
          onClick={() => onSubCategoryChange('SwiggyInstamart')}
        />
        <img
          className={`brand-image ${selectedSubCategory === 'Blinkit' ? 'selected' : ''}`}
          src={Blinkit}
          alt='Blinkit'
          onClick={() => onSubCategoryChange('Blinkit')}
        />
        <img
          className={`brand-image ${selectedSubCategory === 'JioMart' ? 'selected' : ''}`}
          src={JioMart}
          alt='JioMart'
          onClick={() => onSubCategoryChange('JioMart')}
        />
      </div>

      <div className="filter-section-price">
        <label>Set Price Range:</label>
        <input
          type="range"
          id="price"
          min="0"
          max="100000"
          step="500"
          value={selectedPrice === null ? 0 : selectedPrice}
          onChange={(e) => onPriceChange(parseInt(e.target.value, 10))}
        />
        <div className="price-label">
          {selectedPrice} /-
        </div>
      </div>
    </div>
  </div >
);


export default GroceryFilter;
