import React, { useEffect, useState } from 'react';
import MobileFilter from './FilterComponents/MobileFilter';

function Mobiles() {
  const [mobiles, setMobiles] = useState([]);
  const [filteredMobiles, setFilteredMobiles] = useState([]);
  const [selectedBrand, setSelectedBrand] = useState('all');
  const [selectedPrice, setSelectedPrice] = useState(500);
  const [loading, setLoading] = useState(false); // Add loading state

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true); // Set loading to true while fetching data
      try {
        // Construct the URL based on selected filter values
        const url = `http://192.168.0.101:8080/api/product/byBrand?productType=Mobile&brand=${selectedBrand}&price=${selectedPrice}`;

        console.log(selectedBrand);
        console.log(selectedPrice);

        
        const response = await fetch(url);
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setMobiles(data);
        setFilteredMobiles(data);
      } catch (error) {
        console.error('Error fetching mobile data:', error);
        // Handle the error (e.g., display an error message to the user)
      } finally {
        setLoading(false); // Set loading to false after data fetching, whether it succeeded or failed
      }
    };

    fetchData();
  }, [selectedBrand, selectedPrice]);

  const handleFilterChange = (filteredMobiles) => {
    setFilteredMobiles(filteredMobiles);
  };

  return (
    <div className="App">
      <h1>Mobile List</h1>
      <MobileFilter
        mobiles={mobiles}
        selectedBrand={selectedBrand}
        selectedPrice={selectedPrice}
        onFilterChange={handleFilterChange}
        setSelectedBrand={setSelectedBrand}
        setSelectedPrice={setSelectedPrice}
      />
      {loading ? (
        <p>Loading...</p> // Display a loading message while data is being fetched
      ) : (
        <ul>
          {filteredMobiles.map((mobile) => (
            <li key={mobile.name}>
              {mobile.brand} {mobile.name} - ${mobile.price}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default Mobiles;
