import React, { Component } from "react";
import Mobile from "./Mobile";

class Mobiles extends Component {
  constructor(props) {
    super(props);
    this.state = {
      mobiles: [],
      filters: {
        company: "",
        price: "",
      },
    };
  }

  componentDidMount() {
    // Get the list of mobiles from the backend API.
    fetch("/api/mobiles")
      .then((response) => response.json())
      .then((mobiles) => {
        this.setState({ mobiles });
      });
  }

  handleFilterChange = (e) => {
    const { name, value } = e.target;
    this.setState({
      filters: {
        ...this.state.filters,
        [name]: value,
      },
    });
  };

  render() {
    const { mobiles, filters } = this.state;
    return (
      <div>
        <MobileFilter filters={filters} onFilterChange={this.handleFilterChange} />
        <div className="mobiles-list">
          {mobiles.filter((mobile) => {
            const { company, price } = filters;
            return (
              company === "" || mobile.company === company &&
              price === "" || mobile.price >= filters.price &&
              mobile.price <= (filters.price + 100)
            );
          })
            .map((mobile) => (
              <Mobile key={mobile.id} mobile={mobile} />
            ))}
        </div>
      </div>
    );
  }
}

export default Mobiles;
