import React, { useEffect, useState } from 'react';
import MobileFilter from './FilterComponents/MobileFilter';

function Mobiles() {
  const [mobiles, setMobiles] = useState([]);
  const [filteredMobiles, setFilteredMobiles] = useState([]);

  useEffect(() => {
    
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:8080/api/product/byBrand?brand=Motorola');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setMobiles(data);
        setFilteredMobiles(data); 
      } catch (error) {
        console.error('Error fetching mobile data:', error);
      }
    };

    fetchData(); // Call the fetchData function inside useEffect
  }, []); // Add an empty dependency array

  const handleFilterChange = (filteredMobiles) => {
    setFilteredMobiles(filteredMobiles);
  };

  return (
    <div className="App">
      <h1>Mobile List</h1>
      <MobileFilter mobiles={mobiles} onFilterChange={handleFilterChange} />
      <ul>
        {filteredMobiles.map((mobile) => (
          <li key={mobile.name}>
            {mobile.brand} {mobile.name} - ${mobile.price}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Mobiles;
