import React from 'react';

const JoinWhatsApp = () => {
  const handleJoinWhatsAppGroup = () => {
    // Use a WhatsApp group link, for example: "https://chat.whatsapp.com/abc123"
    const whatsappLink = `whatsapp://send?text=Join%20our%20WhatsApp%20group:%20${groupLink}`;
    
    // Open the WhatsApp app or prompt the user to install it
    window.location.href = whatsappLink;
  };

  return (
    <button onClick={handleJoinWhatsAppGroup}>Join Now on WhatsApp</button>
  );
};

export default JoinWhatsApp;
