import React from 'react';
import '../Css/MobileFilter.css';
import all from '../Images/5110770.png';
import men from '../Images/MensFashion.jpg';
import women from '../Images/WomenFashion.jpg';
import kids from '../Images/kidsFashion.png';
import men_access from '../Images/MensAccessories.jpg';
import women_access from '../Images/Female_Accessories.jpg';


const FashionFilter = ({
  selectedSubCategory,
  selectedPrice,
  onSubCategoryChange,
  onPriceChange
}) => (
  <div className="product-filter">
    <div className="filter-section-brand">
      <div className='brand-section'>
      <img
          className={`brand-image ${selectedSubCategory === '' ? 'selected' : ''}`}
          src={all}
          alt='all'
          onClick={() => onSubCategoryChange('')}
        />

        <img
          className={`brand-image ${selectedSubCategory === 'Mens' ? 'selected' : ''}`}
          src={men}
          alt='Mens'
          onClick={() => onSubCategoryChange('Mens')}
        />
        <img
          className={`brand-image ${selectedSubCategory === 'Womens' ? 'selected' : ''}`}
          src={women}
          alt='Womens'
          onClick={() => onSubCategoryChange('Womens')}
        />
        <img
          className={`brand-image ${selectedSubCategory === 'Kids' ? 'selected' : ''}`}
          src={kids}
          alt='Kids'
          onClick={() => onSubCategoryChange('Kids')}
        />
        <img
          className={`brand-image ${selectedSubCategory === 'Mens_accessories' ? 'selected' : ''}`}
          src={men_access}
          alt='Mens_accessories'
          onClick={() => onSubCategoryChange('Mens_accessories')}
        />
        <img
          className={`brand-image ${selectedSubCategory === 'Womens_accessories' ? 'selected' : ''}`}
          src={women_access}
          alt='Womens_accessories'
          onClick={() => onSubCategoryChange('Womens_accessories')}
        />
      </div>

      <div className="filter-section-price">
        <label>Set Price Range:</label>
        <input
          type="range"
          id="price"
          min="0"
          max="100000"
          step="500"
          value={selectedPrice === null ? 0 : selectedPrice}
          onChange={(e) => onPriceChange(parseInt(e.target.value, 10))}
        />
        <div className="price-label">
          {selectedPrice} /-
        </div>
      </div>
    </div>
  </div >
);


export default FashionFilter;
