import React, { Component } from 'react';

class ProductFilter extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedBrand: '',
      selectedPrice: '',
      products: [],
    };
  }

  componentDidMount() {
    // Fetch initial product data when the component mounts
    this.fetchProducts();
  }

  // Function to fetch products based on filters
  fetchProducts = () => {
    const { selectedBrand, selectedPrice } = this.state;

    // Replace with your backend API endpoint
    const apiUrl = `http://192.168.0.101:8080/api/product/byBrand?productType=Mobile&brand=${selectedBrand}&price=${selectedPrice}`;

    console.log(apiUrl);

    fetch(apiUrl)
      .then((response) => response.json())
      .then((data) => {
        this.setState({ products: data });
      })
      .catch((error) => {
        console.error('Error fetching products:', error);
      });
  };

  // Handle brand filter change
  handleBrandChange = (event) => {
    this.setState({ selectedBrand: event.target.value }, () => {
      this.fetchProducts();
    });
  };

  // Handle price filter change
  handlePriceChange = (event) => {
    this.setState({ selectedPrice: event.target.value }, () => {
      this.fetchProducts();
    });
  };

  render() {
    const { selectedBrand, selectedPrice, products } = this.state;

    return (
      <div>
        <h1>Product Filters</h1>
        <div>
          <label htmlFor="brand">By Brand:</label>
          <select id="brand" value={selectedBrand} onChange={this.handleBrandChange}>
            <option value="">All Brands</option>
            <option value="Oppo">Oppo</option>
            <option value="Apple">Apple</option>
            {/* Add more brand options as needed */}
          </select>
        </div>
        <div>
          <label htmlFor="price">By Price:</label>
          <input
            type="number"
            id="price"
            placeholder="Enter Price"
            value={selectedPrice}
            onChange={this.handlePriceChange}
          />
        </div>
        <div>
          <h2>Filtered Products:</h2>
          <ul>
            {products.map((product) => (
              <li key={product.id}>{product.name}</li>
            ))}
          </ul>
        </div>
      </div>
    );
  }
}

export default ProductFilter;
