import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom';
import './Css/SelectBrand.css'

function SelectBrand() {
    const { brandName } = useParams();
    const [deals, setDeals] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [selectedSite, setSelectedSite] = useState('');
    const [selectedPriceRange, setSelectedPriceRange] = useState('');
    const [selectedCategory, setSelectedCategory] = useState('');
    const url = `http://192.168.0.103:8080/api/product/Deals/website?Website=${brandName}`;

    useEffect(() => {
        
        fetch(url)
            .then(response => response.json())
            .then(data => {
                setDeals(data);
                setIsLoading(false); 
            })
            .catch(error => {
                console.error('Error fetching deals:', error);
                setIsLoading(false); // Set loading to false on error as well
            });
    }, [url]);
    const filteredDeals = selectedSite ? deals.filter(deal => deal.affiliateSite === selectedSite) : deals;
    const [currentPage, setCurrentPage] = useState(1);
    const dealsPerPage = 50;

    const indexOfLastDeal = currentPage * dealsPerPage;
    const indexOfFirstDeal = indexOfLastDeal - dealsPerPage;
    const currentDeals = deals.slice(indexOfFirstDeal, indexOfLastDeal);

    function goToPage(pageNumber) {
        setCurrentPage(pageNumber);
    }

    function goToPreviousPage() {
        if (currentPage > 1) {
            setCurrentPage(currentPage - 1);
        }
    }

    function goToNextPage() {
        if (currentPage < Math.ceil(deals.length / dealsPerPage)) {
            setCurrentPage(currentPage + 1);
        }
    }

    return (
        <div className='deals-container-main'>
            <div className="site-filter">
                <select value={selectedSite} onChange={event => setSelectedSite(event.target.value)}>
                    <option value="">All Sites</option>
                    <option value="amazon">Amazon</option>
                    <option value="flipkart">Flipkart</option>
                </select>
            </div>
            <div className="price-filter">
                <select value={selectedPriceRange} onChange={event => setSelectedPriceRange(event.target.value)}>
                    <option value="">All Price Ranges</option>
                    <option value="0-25">$0 - $25</option>
                    <option value="25-50">$25 - $50</option>
                    {/* Add more options as needed */}
                </select>
            </div>
            <div className="category-filter">
                <select value={selectedCategory} onChange={event => setSelectedCategory(event.target.value)}>
                    <option value="">All Categories</option>
                    <option value="electronics">Electronics</option>
                    <option value="clothing">Clothing</option>
                    {/* Add more options as needed */}
                </select>
            </div>
            <div className="deals-container">
                {isLoading ? (
                    <p>Loading...</p>
                ) : (
                    currentDeals.map((deal) => (
                        <div key={deal.itemId} className="deal-card">
                            <a href={deal.affiliateLink} className='brandUrl' target="_blank" rel="noopener noreferrer">
                                <div className='deal-image-div'>
                                    <img src={deal.imageUrl} alt={deal.itemTitle} className='deal-image' />
                                </div>
                                <div className='deal-title-div'>
                                    <h1 className='deal-title'>{deal.itemTitle}</h1>
                                </div>
                                <div id='brand-name-rating'>
                                    <div className='brandName-info'>
                                        {deal.affiliateSite}
                                    </div>
                                    <div className="deal-rating">
                                        {renderStars(deal.rating)}
                                    </div>
                                </div>
                                <div className="deal-info">
                                    <div className="reqular-price">
                                        Rs.{deal.originalMRP}/-
                                    </div>
                                    <div className="deal-price">
                                        Rs.{deal.offerPrice}/-
                                    </div>
                                    <span className="offer-label">
                                        {Math.round(((deal.originalMRP - deal.offerPrice) / deal.originalMRP) * 100)}% Off
                                    </span>
                                </div>
                            </a>
                        </div>
                    )))}
            </div>
            <div className="pagination">
                <button
                    onClick={goToPreviousPage}
                    className="pagination-button arrow-button"
                    disabled={currentPage === 1}
                >
                    &#8249;
                </button>
                {Array.from({ length: Math.ceil(deals.length / dealsPerPage) }, (_, index) => (
                    <button
                        key={index + 1}
                        onClick={() => goToPage(index + 1)}
                        className={`pagination-button ${currentPage === index + 1 ? 'active' : ''}`}
                    >
                        {index + 1}
                    </button>
                ))}
                <button
                    onClick={goToNextPage}
                    className="pagination-button arrow-button"
                    disabled={currentPage === Math.ceil(deals.length / dealsPerPage)}
                >
                    &#8250;
                </button>
            </div>
        </div>
    );
}
function renderStars(rating) {
    const fullStars = Math.floor(rating);
    const halfStar = rating - fullStars >= 0.5;

    const stars = [];
    for (let i = 1; i <= 5; i++) {
        if (i <= fullStars) {
            stars.push(<span key={i} className="star">&#9733;</span>);
        } else if (halfStar && i === Math.ceil(rating)) {
            stars.push(<span key={i} className="star">&#9733;</span>);
        } else {
            stars.push(<span key={i} className="star">&#9734;</span>);
        }
    }

    return stars;
}


export default SelectBrand
