import React from 'react';
import { useHistory, useEffect } from 'react-router-dom'; // Import history from react-router-dom

function Home() {
  const history = useHistory(); // Get the history object from the router

  useEffect(() => {
    // Make an HTTP request to the server to check its status
    fetch('/status')
      .then(response => {
        if (response.status !== 200) {
          setIsServerDown(true);
          // Explicitly use history.push to redirect to the server-down page
          history.push('/server-down');
        }
      })
      .catch(error => {
        setIsServerDown(true);
        // Explicitly use history.push to redirect to the server-down page
        history.push('/server-down');
      });
  }, []);

  return (
    <div className="home-main-div">
      <div className="main">
        <BrandBoard />
        <ItemCarousel />
        <ProductBoard />
      </div>
      <div className="deals-menu">
        <DealsDisplay />
      </div>
    </div>
  );
}

export default Home;
