import React, { useState, useEffect } from "react";
import logo from './twitter.png';

const JoinTwitter = () => {
  const [isDesktop, setIsDesktop] = useState(false);
  const channelLink = "https://twitter.com/my_twitter_channel";

  useEffect(() => {
    setIsDesktop(false);
  }, []);

  const buttonLabel = isDesktop ? "Join Our Twitter Channel" : "Follow Us on Twitter";

  return (
    <button className="twitter_text" onClick={() => window.open(channelLink, "_blank")}>
      <img src={logo} className='twitter-img' alt="Twitter logo" />
      {buttonLabel}
    </button>
  );
};

export default JoinTwitter;
