import React, { Component } from 'react';

class SearchBar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userInput: '',
      searchResults: [],
    };
  }

  handleSearch = () => {
    // Split user input into words
    const keywords = this.state.userInput.split(' ');

    // Construct the query string
    const queryString = keywords.map(keyword => `keyword=${keyword}`).join('&');

    // Make an API request to your server
    fetch(`/api/product/search?${queryString}`)
      .then(response => response.json())
      .then(data => {
        this.setState({ searchResults: data });
      })
      .catch(error => {
        console.error('Error fetching search results:', error);
      });
  };

  handleInputChange = (e) => {
    this.setState({ userInput: e.target.value });
  };

  render() {
    return (
      <div>
        <input
          type="text"
          placeholder="Search..."
          value={this.state.userInput}
          onChange={this.handleInputChange}
        />
        <button onClick={this.handleSearch}>Search</button>
        <ul>
          {this.state.searchResults.map((result, index) => (
            <li key={index}>{result.itemTitle}</li>
          ))}
        </ul>
      </div>
    );
  }
}

export default SearchBar;
