import React from 'react';
import '../Css/MobileFilter.css';
import All from '../Images/5110770.png';
import TV from '../Images/smarttv.png';
import Refrigirator from '../Images/refrigerator.png';
import Ac from '../Images/airconditioning.png';
import Washing from '../Images/Washingmachine.png';
import Microwave from '../Images/microwave.png';
import Water from '../Images/WaterPurifier.png'
import Others from '../Images/others.png'


const AppliancesFilter = ({
    selectedSubCategory,
    selectedPrice,
    onSubCategoryChange,
    onPriceChange
}) => (
    <div className="product-filter">
        <div className="filter-section-brand">
            <div className='brand-section'>
                <img
                    className={`brand-image ${selectedSubCategory === '' ? 'selected' : ''}`}
                    src={All}
                    alt='all'
                    onClick={() => onSubCategoryChange('')}
                />
                <img
                    className={`brand-image ${selectedSubCategory === 'Refrigerators' ? 'selected' : ''}`}
                    src={Refrigirator}
                    alt='Refrigerators'
                    onClick={() => onSubCategoryChange('Refrigerators')}
                />
                <img
                    className={`brand-image ${selectedSubCategory === 'WashingMachines' ? 'selected' : ''}`}
                    src={Washing}
                    alt='WashingMachines'
                    onClick={() => onSubCategoryChange('WashingMachines')}
                />
                <img
                    className={`brand-image ${selectedSubCategory === 'Televisions' ? 'selected' : ''}`}
                    src={TV}
                    alt='Televisions'
                    onClick={() => onSubCategoryChange('Televisions')}
                />
                <img
                    className={`brand-image ${selectedSubCategory === 'AirConditioners' ? 'selected' : ''}`}
                    src={Ac}
                    alt='AirConditioners'
                    onClick={() => onSubCategoryChange('AirConditioners')}
                />
                <img
                    className={`brand-image ${selectedSubCategory === 'WaterPurifiers' ? 'selected' : ''}`}
                    src={Water}
                    alt='WaterPurifiers'
                    onClick={() => onSubCategoryChange('WaterPurifiers')}
                />
                <img
                    className={`brand-image ${selectedSubCategory === 'MicrowaveOvens' ? 'selected' : ''}`}
                    src={Microwave}
                    alt='MicrowaveOvens'
                    onClick={() => onSubCategoryChange('MicrowaveOvens')}
                />
                <img
                    className={`brand-image ${selectedSubCategory === 'Others' ? 'selected' : ''}`}
                    src={others}
                    alt='Others'
                    onClick={() => onSubCategoryChange('Others')}
                />
            </div>

            <div className="filter-section-price">
                <label>Set Price Range:</label>
                <input
                    type="range"
                    id="price"
                    min="0"
                    max="100000"
                    step="500"
                    value={selectedPrice === null ? 0 : selectedPrice}
                    onChange={(e) => onPriceChange(parseInt(e.target.value, 10))}
                />
                <div className="price-label">
                    {selectedPrice} /-
                </div>
            </div>
        </div>
    </div >
);


export default AppliancesFilter;
