import React from 'react'
import Carousel from 'react-elastic-carousel'
import './ItemCarousel.css'

 

function ItemCarousel() {
  const items = [
    { id: 1, content: 'Item 1' },
    { id: 2, content: 'Item 2' },
    { id: 3, content: 'Item 3' },
    { id: 4, content: 'Item 4' },
    // Add more items as needed
  ];

  const [carouselRef, setCarouselRef] = useState(null);

  const handleSlideChange = (currentItemIndex) => {
    if (carouselRef && currentItemIndex === items.length - 1) {
      // When last index is reached, go back to the first index
      carouselRef.goTo(0);
    }
  };
  return (
    <div className="carousel-container">
    <Carousel
    ref={ref => setCarouselRef(ref)}
      enableAutoPlay={true}
      autoPlaySpeed={100}
      pagination={true}
      isRTL={false}
      enableSwipe={true}
      enableTilt={true}
      itemsToScroll={1}
      isInfinite={true}
      onSlideChange={handleSlideChange}
    >
      {items.map(item => (
        <div className="carousel-item" key={item.id}>
          <a href="https://www.google.com">
            <h2>{item.content}</h2>
          </a>
        </div>
      ))}
    </Carousel>
  </div>
  )
}

export default ItemCarousel
