import React from 'react';
import '../Css/MobileFilter.css';
import all from '../Images/5110770.png'
import men from '../Images/MensFashion.jpg';
import women from '../Images/WomenFashion.jpg';
import kids from '../Images/kidsFashion.png';
import men_access from '../Images/MensAccessories.jpg';
import women_access from '../Images/'

const FashionFilter = ({
  selectedBrand,
  selectedPrice,
  onBrandChange,
  onPriceChange
}) => (
  <div className="product-filter">
    <div className="filter-section-brand">
      <div className='brand-section'>
        <img
          className={`brand-image ${selectedBrand === '' ? 'selected' : ''}`}
          src={all}
          alt='all'
          onClick={() => onBrandChange('')}
        />

        <img
          className={`brand-image ${selectedBrand === 'realme' ? 'selected' : ''}`}
          src='https://rukminim2.flixcart.com/fk-p-flap/240/240/image/059c763612908e31.jpg?q=90'
          alt='realme'
          onClick={() => onBrandChange('realme')}
        />
        <img
          className={`brand-image ${selectedBrand === 'vivo' ? 'selected' : ''}`}
          src='https://rukminim2.flixcart.com/fk-p-flap/240/240/image/b9a573a09e83cf82.jpg?q=90'
          alt='vivo'
          onClick={() => onBrandChange('vivo')}
        />
        <img
          className={`brand-image ${selectedBrand === 'poco' ? 'selected' : ''}`}
          src='https://rukminim2.flixcart.com/fk-p-flap/240/240/image/dc8a36bb50c1d96a.jpg?q=90'
          alt='poco'
          onClick={() => onBrandChange('poco')}
        />
        <img
          className={`brand-image ${selectedBrand === 'samsung' ? 'selected' : ''}`}
          src='https://rukminim2.flixcart.com/fk-p-flap/240/240/image/ef86060ca7c7b895.jpg?q=90'
          alt='samsung'
          onClick={() => onBrandChange('samsung')}
        />
        <img
          className={`brand-image ${selectedBrand === 'oppo' ? 'selected' : ''}`}
          src='https://rukminim2.flixcart.com/fk-p-flap/240/240/image/70db8464d13e9b83.jpg?q=90'
          alt='oppo'
          onClick={() => onBrandChange('oppo')}
        />
        <img
          className={`brand-image ${selectedBrand === 'apple' ? 'selected' : ''}`}
          src='https://rukminim2.flixcart.com/fk-p-flap/240/240/image/f29ce08479bfb046.jpg?q=90'
          alt='apple'
          onClick={() => onBrandChange('apple')}
        />
         <img
          className={`brand-image ${selectedBrand === 'nothing' ? 'selected' : ''}`}
          src='https://rukminim2.flixcart.com/fk-p-flap/240/240/image/df90397dfd74a8ed.jpg?q=90'
          alt='nothing'
          onClick={() => onBrandChange('nothing')}
        />
         <img
          className={`brand-image ${selectedBrand === 'motorola' ? 'selected' : ''}`}
          src='https://rukminim2.flixcart.com/fk-p-flap/240/240/image/8c01674f526fe506.jpg?q=90'
          alt='motorola'
          onClick={() => onBrandChange('motorola')}
        />
      </div>

      <div className="filter-section-price">
        <label>Set Price Range:</label>
        <input
          type="range"
          id="price"
          min="0"
          max="100000"
          step="1000"
          value={selectedPrice === null ? 0 : selectedPrice}
          onChange={(e) => onPriceChange(parseInt(e.target.value, 10))}
        />
        <div className="price-label">
          {selectedPrice} /-
        </div>
      </div>
    </div>
  </div >
);


export default FashionFilter;
