import React, { useState } from 'react';
import './HeaderStyles.css'; // Import the custom CSS file

const Header = () => {
  const [suggestions, setSuggestions] = useState([]); // Placeholder for search suggestions

  const handleInputChange = (event) => {
    // Simulate search suggestions (replace with actual API call)
    const query = event.target.value;
    const mockSuggestions = ['Shoes', 'Electronics', 'Clothing', 'Accessories'];
    const filteredSuggestions = mockSuggestions.filter(
      suggestion => suggestion.toLowerCase().includes(query.toLowerCase())
    );
    setSuggestions(filteredSuggestions);
  };

  return (
    <header className="header">
      <div className="logo">
        <img src="path-to-your-logo.png" alt="Logo" />
      </div>
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search for products..."
          className="search-input"
          onChange={handleInputChange}
        />
        <button className="search-button">Search</button>
        <div className={`search-suggestions ${suggestions.length > 0 ? 'active' : ''}`}>
          {suggestions.map((suggestion, index) => (
            <div className="suggestion-item" key={index}>
              {suggestion}
            </div>
          ))}
        </div>
      </div>
    </header>
  );
};

export default Header;
