import React, { useEffect, useState } from 'react';
import MobileFilter from './FilterComponents/MobileFilter';

function Mobiles() {
  const [mobiles, setMobiles] = useState([]);
  const [filteredMobiles, setFilteredMobiles] = useState([]);

  useEffect(() => {
    // Fetch mobile data from your backend API when the component mounts
    fetchMobileData('apple') // Specify the brand you want to fetch
      .then((data) => {
        setMobiles(data);
        setFilteredMobiles(data); // Initially, set filteredMobiles to all mobiles
      })
      .catch((error) => {
        console.error('Error fetching mobile data:', error);
      });
  }, []);

  const handleFilterChange = (filteredMobiles) => {
    setFilteredMobiles(filteredMobiles);
  };

  // Function to fetch mobile data from your backend API for a specific brand
  const fetchMobileData = async (brand) => {
    try {
      const response = await fetch(`http://localhost:8080/api/product/byBrand?brand=${brand}`);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      return data;
    } catch (error) {
      throw error;
    }
  };

  return (
    <div className="App">
      <h1>Mobile List</h1>
      <MobileFilter mobiles={mobiles} onFilterChange={handleFilterChange} />
      <ul>
        {filteredMobiles.map((mobile) => (
          <li key={mobile.name}>
            {mobile.brand} {mobile.name} - ${mobile.price}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Mobiles;
