import React from 'react';
import '../Css/MobileFilter.css';
import all from '../Images/5110770.png';
import MensSports from '../Images/MensSports.png';
import WomensSports from '../Images/WomenSports.png';



const SportsFilter = ({
  selectedSubCategory,
  selectedPrice,
  onSubCategoryChange,
  onPriceChange
}) => (
  <div className="product-filter">
    <div className="filter-section-brand">
      <div className='brand-section'>
      <img
          className={`brand-image ${selectedSubCategory === '' ? 'selected' : ''}`}
          src={all}
          alt='all'
          onClick={() => onSubCategoryChange('')}
        />
        <div className='nav'>
        <img
          className={`brand-image ${selectedSubCategory === 'MensSports' ? 'selected' : ''}`}
          src={MensSports}
          alt='MensSports'
          onClick={() => onSubCategoryChange('Mens sportswear')}
        />
        </div>
        <img
          className={`brand-image ${selectedSubCategory === 'WomensSports' ? 'selected' : ''}`}
          src={WomensSports}
          alt='WomensSports'
          onClick={() => onSubCategoryChange('Womens sportswear')}
        />

      </div>

      <div className="filter-section-price">
        <label>Set Price Range:</label>
        <input
          type="range"
          id="price"
          min="0"
          max="100000"
          step="500"
          value={selectedPrice === null ? 0 : selectedPrice}
          onChange={(e) => onPriceChange(parseInt(e.target.value, 10))}
        />
        <div className="price-label">
          {selectedPrice} /-
        </div>
      </div>
    </div>
  </div >
);


export default SportsFilter;
