import React, { useState, useEffect } from 'react';
import ImageGallery from 'react-image-gallery';
import 'react-image-gallery/styles/css/image-gallery.css';
import './Css/ItemCarousel.css';
import API from '../API_Config';
import useSWR from 'swr';

function ItemCarousel() {
  const [index, setIndex] = useState(0);
  const [isLoading, setIsLoading] = useState(true);

  const { data: carouselData, error } = useSWR(
    `${API}/api/carousel/showAll`,
    async (url) => {
      const response = await fetch(url);
      const jsonData = await response.json();
      if (Array.isArray(jsonData)) {
        return jsonData;
      }
      return [];
    },
    {
      initialData: [],
    }
  );

  useEffect(() => {
    if (carouselData) {
      setIsLoading(false);
    } else if (error) {
      console.error('Error fetching data:', error);
      setIsLoading(false);
    }
  }, [carouselData, error]);

  const handleChangeIndex = (selectedIndex) => {
    setIndex(selectedIndex);
  };

  return (
    <div className="carousel-container">
      {isLoading ? (
        <p>Loading...</p>
      ) : carouselData.length === 0 ? (
        <div className="empty-deals">
          <p className="no-deals-div">Currently No Deals Are available</p>
        </div>
      ) : (
        <ImageGallery
          key={carouselData.length}
          items={carouselData.map((item) => ({
            original: item.imageUrl,
            thumbnailLabel: item.title,
            renderItem: () => (
              <a
                href={item.affiliateLink}
                target="_blank"
                rel="noopener noreferrer"
                style={{ textDecoration: 'none' }}
              >
                <div className="image-div">
                  <img
                    className="image-slider"
                    src={item.imageUrl}
                    alt={item.title}
                  />
                </div>
              </a>
            ),
          }))}
          currentIndex={index}
          onClick={handleChangeIndex}
          slidesPerView={1}
          spaceBetween={10}
          autoplayInterval={5000}
          infinite={true}
        />
      )}
    </div>
  );
}

export default ItemCarousel;
