import React from 'react';
import '../Css/MobileFilter.css';

const ProductFilter = ({
  selectedBrand,
  selectedPrice,
  onBrandChange,
  onPriceChange
}) => (
  <div className="product-filter">
    <h1>Product Filters</h1>
    <div className="filter-section">
      <label htmlFor="brand">By Brand:</label>
      <select id="brand" value={selectedBrand} onChange={onBrandChange}>
        <option value="">All Brands</option>
        <option value="Oppo">Oppo</option>
        <option value="Apple">Apple</option>
        {/* Add more brand options as needed */}
      </select>
    </div>
    <div className="filter-section">
      <label>By Price:</label>
      <div className="price-buttons">
        <button
          className={selectedPrice === 'below10000' ? 'active' : ''}
          onClick={() => onPriceChange('10000')}
        >
          Below 10,000/-
        </button>
        <button
          className={selectedPrice === '10000to15000' ? 'active' : ''}
          onClick={() => onPriceChange('10000to15000')}
        >
          10,000/- to 15,000/-
        </button>
        {/* Add more price range buttons as needed */}
      </div>
    </div>
  </div>
);

ProductFilter.defaultProps = {
  selectedPrice: 'below10000'
};

export default ProductFilter;
