import React, { useState, useEffect } from "react";
import logo from '../FooterComponent/Images/';

const JoinTwitter = () => {
  const [isDesktop, setIsDesktop] = useState(false);
  const channelLink = "https://twitter.com/my_twitter_channel";

  const handleButtonClick = () => {
    window.open(channelLink, "_blank");
  };

  useEffect(() => {
    setIsDesktop(false);
  }, []);

  return (
    <button className="twitter_text" onClick={handleButtonClick}>
      <img src={logo} className='twitter-img' alt="Twitter logo" />
      {/* <p className="twitter_title">Follow Our Twitter Channel</p> */}
    </button>
  );
};

export default JoinTwitter;
